#include "QBIT_ROT.h"#include "STRINGY.h"#ifdef   _mac_gui_app //''''''''''''''''''''''''''''''''''''''''''''\\.	#include "C_QBIT_ROT_AMP_GEN.h"#endif //_mac_gui_app \\............................................//#pragma mark --creation/destruction--//******************************************QBIT_ROT::QBIT_ROT(){}//******************************************QBIT_ROT::~QBIT_ROT(){}#pragma mark ----used only with macgui----#ifdef   _mac_gui_app //''''''''''''''''''''''''''''''''''''''''''''\\.#pragma mark --creation/destruction--//******************************************QBIT_ROT::QBIT_ROT(const Point  &		m_down_pt)	//in	:NODE( m_down_pt){}#pragma mark --drawing--//******************************************VOID	QBIT_ROT::draw(){//drawer	its_efield_p->Draw(0);	if(!its_efield_p->GetSuperView()->FocusDraw())return;	if(its_state_num_for_cap!=max_ushort) draw_state_cap();	Rect r;	::SetRect(&r, 	its_center.h -default_mask_rad, its_center.v -default_mask_rad,					its_center.h +default_mask_rad, its_center.v +default_mask_rad);	::PlotIconID(&r, atNone, ttNone, qbit_rot_icon_rid);}#pragma mark --amp gen--//******************************************VOID	QBIT_ROT::call_amp_gen(QB_NET *  net_p, 		//inC_PI_WIND * pi_wind_p)	//in{	if(its_amp_gen_p==0){//set to nil in NODE::NODE() and NODE::NODE(const Point  & center)		its_amp_gen_p = new C_QBIT_ROT_AMP_GEN(this, net_p, pi_wind_p);		//delete:  by NODE::~NODE()							ThrowIfNil_(its_amp_gen_p );		//Created first time it is used,	}	its_amp_gen_p->talk();}//******************************************VOID	QBIT_ROT::obey_amp_gen(){	empty_trans_mat();		its_st_names[0] = "0";	its_st_names[1] = "1";		COMPLEX	z(0,0);	for(USHORT row=0; row< 2; row++){							z = ((C_QBIT_ROT_AMP_GEN *)its_amp_gen_p)->qbit_rot_amp(row, 0);		if(abs(z)>=amps_floor){			its_tm_amps.set_ele(row, 0, z);		}		z = ((C_QBIT_ROT_AMP_GEN *)its_amp_gen_p)->qbit_rot_amp(row, 1);		if(abs(z)>=amps_floor){			its_tm_amps.set_ele(row, 1, z);		}	}}#endif //_mac_gui_app \\............................................//	