//my_notation://I capitalize all class names.// _p = pointer// _h = handle//I sometimes use://pstr ending 				for Pascal string (like Str255) (unsigned char *) or LStr255//cstr ending 				for C string (char *) or STRINGY//buf_p						indeterminate sex (either C or Pascal)//global variables(often prototyped with extern):  no distiguishing mark or g_//constants (defined with "const" or "define"): no distiguishing mark or k_#pragma once#define VOID      void//#define INT       int 	//I won't use int#define SHORT     short		//16bits#define LONG      long		//32bitstypedef unsigned short	USHORT;    typedef unsigned long	ULONG;     #define CHAR      char		//8bits#define FLOAT     float		//32bits//according to Universal Headers/Types.h//double				64bits//double_t=extended, fastest//68K: 					80 bits//68K with 68881 on: 	96 bits//PowerPC:				64 bits#define DOUBLE64  double//can't use double_t due to a bug in MSL def of polar()#define		DOUBLE		double#define COMPLEX   std::complex<DOUBLE>//according to Universal Headers/Types.h//old : Boolean = unsigned char (8 bits)//new : Boolean = bool = its own type, still 8 bits#define	BOOLEAN	  Boolean#define STREAMBUF streambuf#define IOS       ios#define OSTREAM   ostream#define ISTREAM   istream#define IOSTREAM  iostream#define IFSTREAM  ifstream#define OFSTREAM  ofstream// 4 horsemen of the Apocalypse (War, Famine, Plague, Death), // Also 4: constructors (with various combinations of // data-member inputs), copy constructor, destructor and assigner (=).