#pragma once//******************************************class	C_MARGINED_SCROLLER : public LScroller{protected:	LView  *  its_left_margin_p;	LView  *  its_top_margin_p;	public:		C_MARGINED_SCROLLER(LStream  * inStream);	C_MARGINED_SCROLLER();	virtual ~C_MARGINED_SCROLLER();		VOID  	set_top_margin_ptr( LView  *  margin_p);	VOID	set_left_margin_ptr( LView  *  margin_p);		VOID	VertScroll(SInt16	inPart);	VOID 	HorizScroll(SInt16	inPart);	VOID	ListenToMessage( MessageT inMessage, VOID  * ioParam);	};#pragma mark -//******************************************inlineVOID  	C_MARGINED_SCROLLER::set_top_margin_ptr(LView  *	margin_p)	//in{	its_top_margin_p = margin_p;}//******************************************inlineVOID	C_MARGINED_SCROLLER::set_left_margin_ptr(LView  *	 margin_p)	//in{	its_left_margin_p = margin_p;}