#pragma once#include "QFog_constants.h"#include "C_BI_ND_LBOX.h"#include "C_TOP_PTRS.h"//******************************************class C_BI_ND_WIND : public LWindow, public LListener, public C_TOP_PTRS{private:	USHORT  		its_selector_state; // either 0 or 1	USHORT			its_last_touched_nd;	LCaption  *			its_owner_cap_p;	LStdCheckBox  *  	its_check_box_p;	C_BI_ND_LBOX  * 	its_list_box_p;	public:	BOOLEAN  is_selector_ON();	VOID 	ListenToMessage(MessageT  inMessage, VOID  * ioParam );	C_BI_ND_WIND(LStream  * inStream);	virtual   ~C_BI_ND_WIND();	VOID 	show_owner( const LStr255  &  file_name);	VOID 	special_finish_create(const LStr255  & file_name);					VOID 	respond_to_bi_nd_selector(USHORT touched_nd);		VOID 	refill_list_box();		VOID 	rename_a_nd(USHORT nd, Str255 new_name_pstr); 	};#pragma mark -//******************************************inlineBOOLEAN  C_BI_ND_WIND::is_selector_ON(){	return its_selector_state;}//******************************************inlineVOID	C_BI_ND_WIND::refill_list_box() 	{	its_list_box_p->refill();}	