#include "C_EFIELD_PASTE_ATMENT.h"#include "EXCEPTIONS.h"//******************************************C_EFIELD_PASTE_ATMENT::C_EFIELD_PASTE_ATMENT(){}//******************************************VOID	C_EFIELD_PASTE_ATMENT::ExecuteSelf(MessageT	inMessage,				//invoid	* 	ioParam)				//i-o{#pragma unused(ioParam)	mExecuteHost = true;	if(inMessage == cmd_Paste){		if(!clipping_is_valid()){			my_gen_alert("Can't paste current Clipboard contents into this field. They are an illegal entry for the field.");										mExecuteHost = false;		}												}}//******************************************BOOLEAN	 C_EFIELD_PASTE_ATMENT::clipping_is_valid(){	Handle	content_h= ::NewHandle( 0L );	//destroy_h: in this method	ThrowIfMemFail_(content_h);		BOOLEAN  is_valid;	if(get_TEXT_scrap(content_h)){ //GLOBAL_FUN.h		::HLock(content_h);//no need to save handle state as will be destroying handle soon		const CHAR  * 	cstr = *content_h;		is_valid = cstr_is_valid(cstr);	}else{		is_valid = false;	}		DisposeHandle_(content_h);		return is_valid;}	