#include "C_ACTION_FLAGS.h"//******************************************C_ACTION_FLAGS::C_ACTION_FLAGS(USHORT		num_of_act_types)			//in	:its_num_of_act_types(num_of_act_types){	SignalIf_(num_of_act_types > max_num_of_act_types);}//******************************************VOID   C_ACTION_FLAGS::set_is_modified(BOOLEAN  		is_mod)			//in{	for(USHORT i=0; i< its_num_of_act_types; i++){		its_is_modified[i] = is_mod;	}	//For is_mod==true, could say PostAction(0) here.}