#pragma once#include "C_DOC.h"class C_PROJECT_MENU {protected:	static 	LMenu  * 	its_menu_p;	static	MenuHandle	its_mac_menu_h;	public:	static C_DOC  *		menu_item_to_doc_ptr( SInt16 menu_item );	static SInt16		doc_ptr_to_menu_item(const C_DOC  *  doc_p );		static VOID			init();	static VOID			reset_command_keys();	static VOID			insert_doc(const C_DOC  *  doc_p );	static VOID			rename_doc(const C_DOC  *  doc_p );	static VOID			remove_doc(const C_DOC  *  doc_p );};#pragma mark -//******************************************inlineSInt16 C_PROJECT_MENU::doc_ptr_to_menu_item(const C_DOC  *  doc_p )//in{	return LDocument::GetDocumentList().FetchIndexOf( (C_DOC  *)doc_p );}