#pragma onceclass C_DOC;//This class was inspired by UWindowState,//a Metrowerks contributed class.//In C_MAIN_VIEW, where this class is used the most,//the C_WIND_BDS_RECO for a window//and the window's actual bounds may be different for a while.//In such cases, one may wish to distinguish between the //2 sets of bounds by calling: //the actual bounds, the "window bounds" and //the window's C_WIND_BDS_RECO, the "window bounds record".//******************************************class C_WIND_BDS_RECO{ private:	Rect		its_bds;public:		C_WIND_BDS_RECO();		VOID		change_wind(LWindow  *  wind_p) const;	VOID		set_reco(LWindow  *  wind_p);	VOID		move_to_top_right_corner_of_screen(LWindow  * 	wind_p);			VOID		write_res(C_DOC  * doc_p, ResIDT  rid);	VOID		read_res(C_DOC  * doc_p, ResIDT  rid);};