#pragma once//******************************************class	C_CELL_EDITOR : public LEditField{protected:	STableCell   its_cell;public:		C_CELL_EDITOR(LView  * super_view_p, LCommander  * super_comm_p);	virtual   ~C_CELL_EDITOR();		const STableCell  &  	get_cell() const;	VOID  	set_cell(const STableCell  & cell);		VOID  	move_to(SInt16  horz, SInt16  vert);	virtual BOOLEAN  	HandleKeyPress(const EventRecord  & in_key_event);	virtual BOOLEAN 	ObeyCommand(CommandT inCommand, VOID * ioParam);	virtual VOID		FindCommandStatus(CommandT inCommand,							BOOLEAN & outEnabled, BOOLEAN & outUsesMark,							UInt16 & outMark, Str255 outName_pstr);		};#pragma mark -//******************************************inlineconst STableCell  &  	C_CELL_EDITOR::get_cell()const{	return its_cell;}//******************************************inlineVOID  	C_CELL_EDITOR::set_cell(const STableCell  &		cell)	//in{	its_cell= cell;}