#include "C_SPEED_LIM_SCROLLER.h"//******************************************C_SPEED_LIM_SCROLLER::C_SPEED_LIM_SCROLLER(LStream  *		inStream)	//in	:LScroller(inStream){}//******************************************C_SPEED_LIM_SCROLLER::C_SPEED_LIM_SCROLLER(){}//******************************************C_SPEED_LIM_SCROLLER::~C_SPEED_LIM_SCROLLER(){}//******************************************void	C_SPEED_LIM_SCROLLER::VertScroll(const SInt16		inPart)		//in{	long startTicks = ::TickCount();		LScroller::VertScroll(inPart);		//this puts an upper limit on scroll rate.	while (startTicks + 10>=::TickCount()){}}	//******************************************void	C_SPEED_LIM_SCROLLER::HorizScroll(const SInt16		inPart)		//in{	long startTicks = ::TickCount();		LScroller::HorizScroll(inPart);		//this puts an upper limit on scroll rate.	while (startTicks + 10>=::TickCount()){}	}	