#pragma once#include "QFog_constants.h"#include "C_KEYS.h"//******************************************class	C_PI_ND_EFIELD : public LEditField, public LBroadcaster{protected:	USHORT  its_nd;public:		C_PI_ND_EFIELD(LStream  * inStream);	C_PI_ND_EFIELD();	virtual   ~C_PI_ND_EFIELD();	VOID FinishCreateSelf();		USHORT  get_nd();	VOID  	set_nd_always(USHORT  nd);		VOID 	SpendTime( const EventRecord	&	inMacEvent );		VOID	scroll_to_home();};#pragma mark -//******************************************inlineVOID	C_PI_ND_EFIELD::FinishCreateSelf(){	SetKeyFilter(C_KEYS::my_PrintingCharField);}//******************************************inlineUSHORT  C_PI_ND_EFIELD::get_nd(){	return its_nd;}//******************************************inlineVOID  	C_PI_ND_EFIELD::set_nd_always(USHORT  nd)		//in{	its_nd = nd;}//******************************************inlineVOID	C_PI_ND_EFIELD::scroll_to_home(){	//Ref: LEditField::HandleKeyPress()	::TESetSelect(0, 0, mTextEditH);}