#include "C_TOP_PTRS.h"//******************************************C_TOP_PTRS::C_TOP_PTRS()	:its_doc_p(0),	its_net_p(0),	its_mview_p(0){}//******************************************C_TOP_PTRS::~C_TOP_PTRS(){}//******************************************VOID 	C_TOP_PTRS::set_top_ptrs(C_DOC  * 			doc_p,			//inC_MAIN_VIEW  *  	mview_p,		//inQB_NET  * 			net_p)			//in{	its_doc_p = doc_p;	its_mview_p = mview_p;	its_net_p = net_p;}//******************************************VOID  	C_TOP_PTRS::get_top_ptrs(C_DOC  *  & 		doc_p,			//outC_MAIN_VIEW  *  & 	mview_p,		//outQB_NET  *  & 		net_p)			//outconst{	doc_p = its_doc_p;	mview_p = its_mview_p;	net_p = its_net_p;}