#include "CUSTOM_ND.h"#ifdef   _mac_gui_app //''''''''''''''''''''''''''''''''''''''''''''\\.	#include "StCPTstate.h"#endif //_mac_gui_app \\............................................////******************************************#pragma mark --creation/destruction--//******************************************CUSTOM_ND::CUSTOM_ND(){}//******************************************CUSTOM_ND::~CUSTOM_ND(){}#pragma mark ----used only with macgui----#ifdef   _mac_gui_app //''''''''''''''''''''''''''''''''''''''''''''\\.#pragma mark --creation/destruction--//******************************************CUSTOM_ND::CUSTOM_ND(const Point  &		m_down_pt)	//in	:NODE( m_down_pt),	its_color(C_COLORS::its_white){}#pragma mark --drawing--//******************************************VOID	CUSTOM_ND::draw(){//drawer	its_efield_p->Draw(0);	if(!its_efield_p->GetSuperView()->FocusDraw())return;	if(its_state_num_for_cap!=max_ushort) draw_state_cap();	StCPTstate		saved_st(true, true, false);	saved_st.Normalize(); 	// first draw colored mask:		::RGBForeColor(&its_color);	Rect r;	::SetRect(&r, 	its_center.h -default_mask_rad, its_center.v -default_mask_rad, 					its_center.h +default_mask_rad, its_center.v +default_mask_rad);	::PaintOval(&r);		//now draw black rim:	::RGBForeColor(&C_COLORS::its_black);		::FrameOval(&r);}#endif //_mac_gui_app \\............................................//	